import React from "react";

const CreateTask = ({}) => {
  const [description, setDescription] = useState("");
  return (
    <div>
      CreateTask
      <div>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e?.target?.value)}
        />
      </div>
    </div>
  );
};

export default CreateTask;
